INSERT INTO `#__ce_template` (`id`, `name`, `html`, `type`, `published`, `access`, `language`, `params`, `created`, `created_by`, `modified`, `modified_by`, `checked_out`, `checked_out_time`) VALUES
(1, 'Default Email', '<table style="padding:10px;background-color:#eee;font-family:Arial;font-size:11px;" border="0" cellspacing="0" cellpadding="0" width="100%">\r\n<tbody>\r\n<tr>\r\n<td align="center">\r\n<table style="background-color:#fff;" border="0" cellspacing="0" cellpadding="0" width="600">\r\n<tbody>\r\n<tr>\r\n<td style="color:#888;padding:10px;" align="left">\r\n<h1 style="color:#888;">{site_name}</h1>\r\n<hr style="color:#ab0e1f;background-color:#ab0e1f;height:5px;" />\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style="padding:10px;" valign="top">\r\n<p>{enquiry}<br /><br />{custom_fields}</p>\r\n<p> </p>\r\n<p>{attachments}</p>\r\n<p>{system_info}</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style="background-color:#505050;color:#c8c8c8;padding:20px" height="61" align="left" valign="middle">\r\n<p>{contact_name} <br />{contact_address}, {contact_suburb} - {contact_state} - {contact_country} {contact_postcode}<br />{contact_telephone} | {contact_mobile}</p>\r\n<p>Copyright © {site_link}.</p>\r\n<p>Sent date: {timestamp}</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p> </p>', 'email', 1, 1, '*', '{"style-cf-label":"font-weight:bold;display:block;padding-top:8px","style-cf-value":""}', '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00'),
(2, 'Ideal Support Copy Message', '<div style="margin:0;padding:0;background:#f0f0f0 none repeat scroll 0 0; width:100%;color:#666;">\r\n<div style="margin:0 auto;width:600px;">\r\n<div style="background:#c01122 none repeat scroll 0 0;overflow: hidden;height:80px;width:600px;"><img src="http://idealextensions.com//components/com_contact_enhanced/templates/email/ideal_support/images/ideal_software_development_header.jpg" border="0" alt="http://idealextensions.com//components/com_contact_enhanced/templates/email/ideal_support/images/ideal_software_development_header.jpg" /></div>\r\n<div id="ja-contentwrapper">\r\n<div style="margin:0 auto; padding:10px;background:#fff none repeat scroll 0 0; width:515px;">\r\n<div id="ja-content">\r\n<div style="margin: 0 10px;">\r\n<h1 style="border-bottom: 1px solid #cccccc; margin: 0pt 0pt 15px; padding: 10px; font-size: 150%;">{sitename}</h1>\r\n<div style="border: 1px dashed #cccccc; margin: 3px; padding: 3px; background-color: #ffffdd; width: 180px; float: right;">\r\n<h3>Have you seen these resources?</h3>\r\n<p>→ <a href="http://idealextensions.com/component/option,com_moofaq/Itemid,24/id,2/view,categories/">FAQ</a>;</p>\r\n<p>→ <a href="http://idealextensions.com/component/option,com_kunena/Itemid,4/">Forum</a>;</p>\r\n<p>→ <a href="http://idealextensions.com/joomla/contact-enhanced/contact-enhanced-documentation.html">Contact Enhanced Documentation</a>;</p>\r\n</div>\r\n{txt:Thanks for contacting us. We''ll reply to your email as soon as possible}.</div>\r\n<div style="margin:0 10px;"><br /> {txt:Below is a copy of your message}:\r\n<p>{custom_fields}</p>\r\n<p>{attachments}</p>\r\n<div style="border-top:1px dashed #CCC;">\r\n<h3>In the meanwhile take a look at our other extensions:</h3>\r\n<p><a href="http://idealextensions.com/joomla/extensions/component-contact-enhanced.html">Contact Enhanced</a> - <small>Contact  Form with a few advantages, such as: Google Maps integration, Custom  Fields, Captcha (ReCAPTCHA, SecurImage or MathGuard), User info ( IP  Address, Browser, Operating System and Screen resolution) and many more  enhancement.</small></p>\r\n<p><a href="http://idealextensions.com/joomla/extensions/component-moofaq-frequently-asked-questions.html">MooFAQ</a> - <small>Display FAQ or any content category in an Accordion Style layout.</small></p>\r\n<p><a href="http://idealextensions.com/joomla/extensions/module-opensource-ajax-recommend.html">Ajax Recommend</a> - <small>Allow your users to recommend your website to their friends.</small></p>\r\n<p><a href="http://idealextensions.com/joomla/extensions/module-opensource-ajax-recommend.html">Ajax Contact</a> - <small>Allows visitors to contact your site administrator by e-mail very fast and secure, without the need to reload the page;</small></p>\r\n<p><a href="http://idealextensions.com/joomla/extensions/component-name-meaning-dictionary.html">Name Meaning Dictionary</a> - <small>Has  over 4100 girls and boys names and meanings in the database, from 47  different origins, ideal for your baby or pregnancy related website.</small></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<div style="border-top:3px solid #c01122;padding:15px 10px; background:#34383b none repeat scroll 0 0;color:#ccc; position: relative;">\r\n<div style="margin:0;padding:0;font-size:11px;float:right;display:block;"><a href="http://idealextensions.com/index.php" style="padding:0 10px;display:inline;color:#fff;"><span>Start Page</span></a> | <a href="http://idealextensions.com/terms-conditions.html" style="padding:0 10px;display:inline;color:#fff;"><span>Terms &amp; Conditions</span></a> | <a href="http://idealextensions.com/privacy-policy.html" style="padding:0 10px;display:inline;color:#fff;"><span>Privacy Policy</span></a></div>\r\n<br style="clear: both;" />\r\n<div style="border-top: 1px dashed #CCC;"><br /> <small style="font-size: 70%; color: #999;"> {txt:Sent date}: {timestamp} </small></div>\r\n</div>\r\n</div>\r\n</div>', 'email', 1, 1, '*', '{"style-cf-label":"","style-cf-value":""}', '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00'),
(3, 'Simple Thanks with Custom Fields', '<h1 style="color:#888;">Thank you for your email</h1>\r\n<p>Below are the submitted fields</p>\r\n<p>{enquiry}<br /><br />{custom_fields}</p>\r\n<p>Sent date: {timestamp}</p>', 'resultpage', 1, 1, '*', '{"style-cf-label":"font-weight:bold;display:block;padding-top:8px","style-cf-value":""}', '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00');
